function getOffHoursBeforeMidnight(costEfficientHoursYesterday, considerationHours) {
    if (costEfficientHoursYesterday && costEfficientHoursYesterday.length > 0) {
        node.debug(`costEfficientHoursYesterday=${costEfficientHoursYesterday}`);
        const hourKey = costEfficientHoursYesterday[costEfficientHoursYesterday.length - considerationHours];
        const hour = Number(new String(hourKey).substring(11));
        const offHoursBeforeMidnight = 24 - hour - 1;
        node.debug(`offHoursBeforeMidnight=${offHoursBeforeMidnight}`);
        return offHoursBeforeMidnight;
    }
    return undefined;
}

function chooseCostEfficientHours(payload, powerConsumptionHours, costEfficientHoursYesterday, mandatoryHours, distributeHours) {
    node.debug(`payload=${JSON.stringify(payload)}`);

    node.debug(`distributeHours=${JSON.stringify(distributeHours)}`);
    if (Array.isArray(distributeHours) && distributeHours.length === 3) {
        const offHoursBeforeMidnight = getOffHoursBeforeMidnight(costEfficientHoursYesterday, distributeHours[0]);
        if (offHoursBeforeMidnight) {
            const offHoursRemainingAfterMidnight = distributeHours[1] - offHoursBeforeMidnight;
            node.debug(`offHoursRemainingAfterMidnight=${offHoursRemainingAfterMidnight}`);
            const mandatoryHoursForDistribution = ['0-6', `0-${offHoursRemainingAfterMidnight < distributeHours[2] ? distributeHours[2] - 1 : offHoursRemainingAfterMidnight - 1}`, distributeHours[2]];
            node.debug(`mandatoryHoursForDistribution=${JSON.stringify(mandatoryHoursForDistribution)}`);
            (mandatoryHours ?? []).push(mandatoryHoursForDistribution);
        }
    }

    const hourKeys = Object.keys(payload);
    node.debug(`hourKeys=${JSON.stringify(hourKeys)}`);
    const hourlyCost = new Map();
    hourKeys.forEach(key => hourlyCost.set(key, payload[key]));
    node.debug(`hourlyCost=${JSON.stringify(hourlyCost, replacer)}`);

    let hourlyCostSelected = new Map();
    if (hourKeys.length > 0) {
        const template = hourKeys[0];
        const dow = datefns.getDay(global.get('toDateFromKey')(template));
        if (mandatoryHours) {
            const logHead = 'mandatory hours: ';
            mandatoryHours.forEach(entry => {
                node.debug(`${logHead}processing entry ${entry}`);
                if (Array.isArray(entry)) {
                    if (entry.length === 3 || entry.length === 4) {
                        const dayOrRange = entry[0];
                        const hourRange = entry[1];
                        let hoursToBeSelected = Number(entry[2]);
                        const hoursMandatory = entry[3] ? Array(entry[3]) : [];
                        node.debug(`${logHead}  hoursToBeSelected(initially)=${hoursToBeSelected}`);
                        if (global.get('rangeContainsValue')(dow, dayOrRange) || dow === Number(dayOrRange)) {

                            const hourRangeWithCost = new Map();
                            global.get('addSelectedHours')(template, hourRange, hourRangeWithCost, hourlyCost);
                            const hourRangeWithCostSorted = sortMapByValue(hourRangeWithCost);
                            node.debug(`${logHead}  hourRangeWithCostSorted=${JSON.stringify(hourRangeWithCostSorted, replacer)}`);

                            hoursMandatory.forEach(hour => {
                                const key = global.get('buildKey')(template, hour)
                                hourlyCostSelected.set(key, hourlyCost.get(key));
                                node.debug(`${logHead}  select: ${key}`);
                                hourRangeWithCostSorted.delete(key);
                                hourlyCost.delete(key);
                                hoursToBeSelected--;
                            });
                            node.debug(`${logHead}  after mandatory: hourlyCostSelected=${hourlyCostSelected.size} hoursToBeSelected=${hoursToBeSelected}`);


                            Array.from(hourRangeWithCostSorted.keys()).forEach(key => {
                                if (Array.from(hourlyCostSelected.keys()).includes(key)) {
                                    node.debug(`${logHead}  already selected: ${key}`);
                                    hourRangeWithCostSorted.delete(key);
                                    hoursToBeSelected--;
                                }
                            });

                            node.debug(`${logHead}  remaining: hourlyCostSelected=${hourlyCostSelected.size} hoursToBeSelected=${hoursToBeSelected}`);
                            if (hoursToBeSelected > 0) {
                                Array.from(hourRangeWithCostSorted.keys()).forEach(key => {
                                    if (hourlyCostSelected.size <= hoursToBeSelected) {
                                        hourlyCostSelected.set(key, hourRangeWithCostSorted.get(key));
                                        hourlyCost.delete(key);
                                        node.debug(`${logHead}  select: ${key}`);
                                    }
                                });
                            }
                        }
                    } else if (entry.length === 2) {
                        const dayOrRange = entry[0];
                        const hourOrRange = entry[1];
                        if (global.get('rangeContainsValue')(dow, dayOrRange) || dow === Number(dayOrRange)) {
                            global.get('addSelectedHours')(template, hourOrRange, hourlyCostSelected, hourlyCost);
                        }
                    } else if (entry.length === 1) {
                        const hourOrRange = entry[0];
                        global.get('addSelectedHours')(template, hourOrRange, hourlyCostSelected, hourlyCost);
                    }
                } else {
                    const hourOrRange = entry;
                    global.get('addSelectedHours')(template, hourOrRange, hourlyCostSelected, hourlyCost);
                }
                node.debug(`${logHead}  result after entry processing: ${JSON.stringify(hourlyCostSelected, replacer)}`);
            });
        }

        const hourlyCostSorted = sortMapByValue(hourlyCost);
        node.debug(`hourlyCostSorted=${JSON.stringify(hourlyCostSorted, replacer)}`);
        Array.from(hourlyCostSorted.keys()).forEach(key => {
            if (hourlyCostSelected.size < powerConsumptionHours) {
                hourlyCostSelected.set(key, hourlyCost.get(key));
                node.debug(`select: ${key}`);
            }
        });
        hourlyCostSelected = sortMapByKey(hourlyCostSelected);
        node.debug(`hourlyCostSelected=${JSON.stringify(hourlyCostSelected, replacer)}`);
    }
    return [...hourlyCostSelected.keys()];
}

